<?php

namespace AppBundle\Repository;

use AppBundle\Entity\Agenda;

use Doctrine\ORM\EntityManagerInterface;

/**
 * AgendaRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class AgendaRepository extends \Doctrine\ORM\EntityRepository
{
    /**
     * Salva um novo agendamento
     * @param $params
     * @return boolean
     */
    public function salvar($params) {
        $agenda = new Agenda();
        $agenda->setData($params['data']);
        $agenda->setHora($params['hora']);
        $agenda->setUsuarioId($params['usuarioId']);
        
        $this->_em->persist($agenda);
        $this->_em->flush();
        
        return $agenda;
    }
    
    /**
     * Apaga um agendamento
     * @param $id
     * @return boolean
     */
    public function remover($id) {
        $agenda = $this->find($id);
        if ($agenda) {
            $this->_em->remove($agenda);
            $this->_em->flush();
            return true;
        } else {
            return false;
        }
    }
}
